
public class Forloop4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j;
		for(i=1;i<=30;i++)
		{
			for(j=1;j<=30;j++)
			{
				
				if(j<=15)
				{
					System.out.print("*");
				}
				else
					
				{
					System.out.print("=");
				}
				
			}
			System.out.println();
			
			
		}
		
		for(i=1;i<=10;i++)
		{
			for(j=1;j<30;j++)
			{
				System.out.print("=");
				
			}
			System.out.println();
			
			
		}

	}
	
	
		

}
